mapper.select:
		
		select num, writer, title, contents, cnt, reg, pw, r
from (select num, writer, title, contents, cnt,reg, pw, rownum r from
(select num, writer, title, contents, cnt,reg, pw from question order by num ) 
)where r >= #{startRow} and r <= #{endRow}

mapper.update:
update question 
set writer = #{writer}, title = #{title}, contents = #{contents}, pw = #{pw} 
where num = #{num}

update question 
set cnt = cnt + 1 
where num = #{num}

mapper.select:
select pw 
from question 
where num = #{num}
